import { r as resolve } from './index2.mjs';
import { d as defineNuxtCommand, c as consola } from './index.mjs';
import { l as loadKit, w as writeTypes } from './kit.mjs';
import { c as clearDir } from './fs.mjs';
import 'path';
import 'tty';
import 'util';
import 'fs';
import 'os';
import 'assert';
import './cjs.mjs';
import 'module';
import 'url';
import './rimraf.mjs';

const build = defineNuxtCommand({
  meta: {
    name: "build",
    usage: "npx nuxi build [rootDir]",
    description: "Build nuxt for production deployment"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt, buildNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir });
    await clearDir(nuxt.options.buildDir);
    await writeTypes(nuxt);
    nuxt.hook("build:error", (err) => {
      consola.error("Nuxt Build Error:", err);
      process.exit(1);
    });
    await buildNuxt(nuxt);
  }
});

export { build as default };
